    const [rating, setRating]= useState(0);

const incStar=(star)=>{
    setRating(star)

}
const decStar=(star)=>{
    setRating(star)
}

    return (
        <div>
            StarRating
            {[...Array(5)].map((_, i) => {
                let number = i + 0.5;
                return (
                    <span>
                        {" "}
                        {rating >= i+1  ? (
                            <BsStarFill onClick={() => starUpdate(i + 1)} />
                        ) : (
                                <BsStar onClick={() => setRating(i-1)} />
                        )}{" "}
                    </span>
                );
            })}
        </div>
    );
};

export default StarRatingClick;
