import React, { useEffect, useState } from 'react'

const Search = () => {
const [search,setSearch] = useState("")
const[loading,setLoading] =useState("true")
const [error, setError]= useState(null);
const[item,setItem]= useState([])  

    const getSearchData= async()=>{
        setLoading(true);
        setError(null);
        try{
            const result = await fetch(`https://dummyjson.com/products/search?q=${search}`)
            const response= await result.json();
            setItem(response?.products)
        }
        catch(error) {
            setError(error)

        }finally{
            setLoading(false);
        }
       
    } 
    useEffect(() => { getSearchData() }, [search])
    console.log(item)
  return (
    <div className='search' >Search

          <input type='search' placeholder='Search here........' onChange={(e) => setSearch(e.target.value)} />
          <div className='dropdown'>
          {search !== ""?  item?.map((product) =>
          (<div key={product?.id} className='product'>
              <h3>{product.title}</h3>
              <h3>{product.price}</h3>
          </div>)
          ): ""}
          </div>
      
    </div>
  )
}

export default Search
