import React, { useState } from "react";
import { BsStarHalf, BsStar, BsStarFill } from "react-icons/bs";

const StarRatingClick = () => {
    const [rating, setRating]= useState(1);
    return (
        <div>
            StarRating
            {[...Array(5)].map((_, i) => {
                let number = i + 0.5;
                return (
                    <span>
                        {" "}
                        {rating >= i  ? (
                            <BsStarFill />
                        ) : (
                            <BsStar onClick={()=> incStar(i+1)} />
                        )}{" "}
                    </span>
                );
            })}
        </div>
    );
};

export default StarRatingClick;
